"use client";
import { useState, useEffect } from "react";

import { useSelector, useDispatch } from "react-redux";
import { useRouter } from "next/navigation";
import PageLoader from "@/components/PageLoader";

import {
  Divider,
  Col,
  Row,
  Image,
  Descriptions,
  DescriptionsProps,
} from "antd";
import { PageHeader } from "@ant-design/pro-layout";

import { selectById } from "@/redux/films/selectors";
import { films } from "@/redux/films/actions";

import { DefaultLayout } from "@/layout";

const FilmPage = ({ params }: { params: { id: string } }) => {
  let router = useRouter();
  const dispatch: any = useDispatch();

  const item = useSelector(selectById);

  useEffect(() => {
    dispatch(
      films.getById({
        options: {
          i: params.id,
        },
      })
    );
  }, [params.id]);

  const items: DescriptionsProps["items"] = [
    {
      key: "Plot",
      label: "Plot",
      span: 2,
      children: <p>{item.result?.Plot}</p>,
    },
    {
      key: "Type",
      label: "Type",
      children: <p>{item.result?.Type}</p>,
    },
    {
      key: "Released",
      label: "Released",
      children: <p>{item.result?.Released}</p>,
    },
    {
      key: "Actors",
      label: "Actors",
      children: <p>{item.result?.Actors}</p>,
    },
    {
      key: "Language",
      label: "Language",
      children: <p>{item.result?.Language}</p>,
    },
    {
      key: "imdbRating",
      label: "imdbRating",
      children: <p>{item.result?.imdbRating}</p>,
    },
    {
      key: "BoxOffice",
      label: "BoxOffice",
      children: <p>{item.result?.BoxOffice}</p>,
    },
  ];

  if (item.isLoading) {
    return <PageLoader />;
  } else
    return (
      <DefaultLayout>
        <PageHeader
          ghost={false}
          onBack={() => router.push("/")}
          title="Back"
          className="appHeader"
          style={{ marginBottom: 50 }}
        />

        <Row gutter={32}>
          <Col className="gutter-row" span={8}>
            <div style={{ borderRadius: 10, overflow: "hidden" }}>
              <Image
                width={"100%"}
                height={500}
                src={item.result?.Poster}
                alt={item.result?.Title || ""}
                preview={false}
                fallback="data:image/png;base64,"
              />
            </div>
          </Col>
          <Col className="gutter-row" span={16}>
            <h1>{item.result?.Title}</h1>
            <Divider />
            <Descriptions title={item.result?.Genre} items={items} column={2} />
            ;
          </Col>
        </Row>
      </DefaultLayout>
    );
};

export default FilmPage;
